import React, { Component } from 'react';

class SourcesPage extends Component {
    render() {
        return (
        <>
          <h1>Sources</h1>
          <blockquote>
            <h3>A Countrywide Traffic Accident Dataset</h3>
            <p>Moosavi, Sobhan, Mohammad Hossein Samavatian, Srinivasan Parthasarathy, and Rajiv Ramnath. “A Countrywide Traffic Accident Dataset.”, arXiv preprint arXiv:1906.05409 (2019).</p>
          </blockquote>
          <blockquote>
            <h3>Accident Risk Prediction Based On Heterogeneous Sparse Data: New Dataset and Insights</h3>
            <p>Moosavi, Sobhan, Mohammad Hossein Samavatian, Srinivasan Parthasarathy, Radu Teodorescu, and Rajiv Ramnath. “Accident Risk Prediction based on Heterogeneous Sparse Data: New Dataset and Insights.” In proceedings of the 27th ACM SIGSPATIAL International Conference on Advances in Geographic Information Systems, ACM, 2019.</p>
          </blockquote>
        </>
        );
      }
}

export default SourcesPage;
